'use strict';

var test = require('tape');

var E = require('es-errors');
var R = require('es-errors/range');
var Ref = require('es-errors/ref');
var S = require('es-errors/syntax');
var T = require('es-errors/type');

test('errors', function (t) {
	t.equal(E, Error);
	t.equal(R, RangeError);
	t.equal(Ref, ReferenceError);
	t.equal(S, SyntaxError);
	t.equal(T, TypeError);

	t.end();
});
